import React, { useState } from "react";
import style from "./SearchBar.module.css";

export default function SearchBar({ onSearch }) {
  //-----Mi codigo:----
  let [val, setVal] = useState("");

  function x(e) {
    e.preventDefault();
    onSearch(val);
    setVal("");
  }

  return (
    <div className={style.bar}>
      <form onSubmit={x}>
        <input
          className={style.searching}
          name="buscador"
          placeholder="¿Qué busca mi rey?"
          type="text"
          value={val}
          onChange={(e) => {
            setVal(e.target.value);
          }}
        />
        <input type="submit" className={style.btn} value="Agregar" />
      </form>
    </div>
  );

