import React, { useState } from "react";
import "./App.css";
import Cards from "./components/Cards.jsx";
import Nav from "./components/Nav.jsx";

export default function App() {
  var [cities, setCities] = useState([]);

  function onSearch(ciudad) {
    apiKey = "bd1c01c112a39203cf62907f6423a754";
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${ciudad} &appid=${apiKey}`
    )
      //fetch(`http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}&units=metric`)
      .then((r) => r.json())
      .then((data) => {

        if(data!=='undefined'){
        const ciudadEjemplo = {
          min: data.main.temp_min,
          max: data.main.temp_max,
          img: data.weather[0].icon,
          id: data.id,
          wind: data.wind.speed,
          temp: data.main.temp,
          name: data.name,
          weather: data.weather[0].main,
          clouds: data.clouds.all,
          latitud: data.coord.lat,
          longitud: data.coord.lon,
        };

        if (!cities.find((x) => x.name === ciudadEjemplo.name)) {
          setCities((oldCities) => [...oldCities, ciudadEjemplo]);
        }}

      })

      ;
  }

  function onClose(id) {
    setCities((r) => r.filter((c) => c.id !== id));
  }
  return (
    <div className="App">
      {/* Tu código acá: */}
      <Nav onSearch={onSearch} />
      <Cards cities={cities} onClose={onClose} />
    </div>
  );
}
