import React from "react";
import CardCSS from "./Card.module.css";

export default function Card(props) {
  // ac치 va tu c칩digo

  function backgroundColor() {
    switch (props.img) {
      case "03n":
        return CardCSS.b1;
      case "09d":
        return CardCSS.b2;
      default:
        return CardCSS.card;
    }
  }

  return (
    <div className={`${CardCSS.card} ${backgroundColor()}`}>
      <div className={CardCSS.first}>
        <span className={CardCSS.title}>{props.name}</span>
        <button className={CardCSS.btn}>x</button>
      </div>
      <div className={CardCSS.data}>
        <div className={CardCSS.info}>
          <p className={CardCSS.text}> Min: {props.min}</p>
          <p className={CardCSS.text}> Max: {props.max}</p>
        </div>

        <img
          className={CardCSS.image}
          src={"http://openweathermap.org/img/wn/" + props.img + "@2x.png"}
          alt=""
        />
      </div>
    </div>
  );
}
